import React, {useState} from "react";
import './Usuario.css';


export default function UsuarioFunction() {
 
       const [usuario, setUsuario] = useState({nome:'',email:'',senha:''});
 
    
    function handleChange(event){
        setUsuario({...usuario,[event.target.name]:event.target.value});
    }

    function handleSubmit(event){
        event.preventDefault();        
    }
    function limpar(event){
        setUsuario({ nome: '', email: '', senha: ''});
    }
    
        return (
            <div>
               <h1>Cadastro de usu√°rio</h1>
                <form onSubmit={handleSubmit}>
                    <label>
                        Nome<br/>
                        <input name="nome" class="" value={usuario.nome} onChange={handleChange} type="text" /><br /><br />
                    </label> <br /><br />
                    <label>
                        E-mail<br />
                        <input name="email" value={usuario.email} onChange={handleChange} type="email" /><br /><br />
                    </label> <br /><br />
                    <label>
                        Senha<br />
                        <input name="senha" value={usuario.senha} onChange={handleChange} type="password" /><br /><br />
                    </label> <br /><br />
                    <br />
                    
                    <input type="submit" value="Salvar"/>
                    <input type="button" value="Limpar" onClick={limpar} />

               </form>

                <table>
                    <tr>
                        <th>Id</th>
                        <th>Nome</th>
                        <th>E-mail</th>
                        <th>Op√ß√µes</th>
                    </tr>
                    <tr>
                        <td></td>
                        <td></td>
                        <td></td>
                        <td>
                            <button>Alterar</button>
                            <button>Excluir</button>
                        </td>
                    </tr>

                </table>

               <p>
                    {usuario.resultado}
               </p>

               
            </div>
        );
    
}
