import React, {useState} from "react";
import './Usuario.css';


export default function UsuarioFunction() {
 
       const [valores, setValores] = useState({nome:'',email:'',senha:'',resultado:''});
 
    
    function handleChange(event){
        setValores({...valores,[event.target.name]:event.target.value});
    }

    function handleSubmit(event){
        event.preventDefault();        
        let resultado = parseFloat(valores.valor1) + parseFloat(valores.valor2);
        setValores({...valores,resultado:resultado});
    }
    function limpar(event){
        setValores({ nome: '', email: '', senha: '', resultado: '' });
    }
    
        return (
            <div>
               <h1>Cadastro de usuário</h1>
                <form onSubmit={handleSubmit}>
                    <label>
                        Nome<br/>
                        <input name="nome" value={valores.nome} onChange={handleChange} type="text" /><br /><br />
                    </label> <br /><br />
                    <label>
                        E-mail<br />
                        <input name="email" value={valores.email} onChange={handleChange} type="email" /><br /><br />
                    </label> <br /><br />
                    <label>
                        Senha<br />
                        <input name="senha" value={valores.senha} onChange={handleChange} type="password" /><br /><br />
                    </label> <br /><br />
                    <br />
                    
                    <input type="submit" value="Salvar"/>
                    <input type="button" value="Limpar" onClick={limpar} />

               </form>

                <table>
                    <tr>
                        <th>Id</th>
                        <th>Nome</th>
                        <th>E-mail</th>
                        <th>Opções</th>
                    </tr>
                    <tr>
                        <td></td>
                        <td></td>
                        <td></td>
                        <td><input type="button" value="Alterar" />
                            <input type="button" value="Excluir" onClick={limpar} /></td>
                    </tr>

                </table>

               <p>
                    {valores.resultado}
               </p>

               
            </div>
        );
    
}
